    def update(self, kwargs, AiOrPeople):
        # 宝牌
        kingcard = kwargs['king_card']
        # self.baopai.setStyleSheet("image: url(:/img2/{1});\n"
        #     "background-color: rgb(85, 170, 127);")
        # self.baopai.setPixmap(QPixmap(r"D:\project\python\MJDecisionAI-DeepLearn\image\play2\1.png"))
        self.show_image(self.id_to_pic[kingcard], self.baopai, self.root[2])
        # self.baopai.setStyleSheet("image: url(:/img2/{});\n"
        #     "background-color: rgb(85, 170, 127);".format(self.id_to_pic[kingcard]))
        # 庄家id
        zhuang_id = kwargs['zhuang_id']
        self.label_2.setText("庄家id：{}".format(zhuang_id))
        # 当前轮次
        round = kwargs['round']
        # 当前玩家id
        current_id = kwargs['current_id']
        # 轮盘显示
        self.show_image("shang.png" if (current_id == 0) else ("you.png" if (current_id == 1) else ("xia.png" if current_id == 2 else "zuo.png") ),
            self.lunpan, self.root[4])
        # 玩家信息
        play0 = kwargs['play_0']
        play1 = kwargs['play_1']
        play2 = kwargs['play_2']
        play3 = kwargs['play_3']
        current_players = [play0, play1, play2, play3]
        players_fulu = [[], [], [], []]
        item = []
        for i in range(4):
            for j in current_players[i]['fulu']:
                item.extend(j)
